import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { useEffect, useState } from 'react';
import logo from '../assets/images/logo.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const Header = () => {
  var [activeLink, setActiveLink] = useState('home');
  var [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
     setScrolled(window.scrollY > 50)
    }
    window.addEventListener("scroll", onScroll);

    return  () => window.removeEventListener("scroll", onScroll);
  }, [])

  const updateActiveLink = (value) => {
    setActiveLink(value);
  }
  return (
    <>
      <Navbar expand="lg" className={scrolled ? 'scrolled' : ''}>
        <Container>
          <Navbar.Brand href="#home">
            {/* give brand logo here  */}
            <img src={logo} alt=""></img>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home" className={activeLink === 'home' ? 'active-nav-link' : 'nav-link'} onClick={() => updateActiveLink('home')}>Home</Nav.Link>
              <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active-nav-link' : 'nav-link'} onClick={() => updateActiveLink('skills')}>Skills</Nav.Link>
              <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active-nav-link' : 'nav-link'} onClick={() => updateActiveLink('projects')}>Projects</Nav.Link>
            </Nav>
            <span className="social-callouts">
              <div className="icons">
                <a href="#"><i className="fa-brands fa-linkedin-in"/></a>
                <a href="#"><i className="fa-brands fa-facebook"/></a>
                <a href="#"><i className="fa-brands fa-instagram"/></a>
              </div>
              <button className="contact" onClick={() => console.log('Connect')}><span>Let's Connect</span></button>
            </span>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
}

export default Header;
