import { useState } from "react";

function TabSection(props) {
  return (
    <>
      <div className={`content ${props.tabClass} ${props.activeLink === props.tabClass ? 'active-link' : ''}`}>
        <div className="name">Working {props.tabName}</div>
        <div className="content">{props.tabContent}</div>
      </div>
    </>
  );
}

function Practice1Tab() {
  const [activeClass, setActiveClass] = useState('tab1');

  const updateActiveLink = (tabClass) => {
    setActiveClass(tabClass);
  }

  const tabContent = [
    {
      name: "Why React?",
      description:
        "Component-based architecture: React follows a component-based architecture, allowing developers to build UI elements as reusable and self-contained components. This promotes code reusability, modularity, and easier maintenance.",
      tabClass: 'tab1'
    },
    {
      name: "Core Feature",
      description: "Another feature description.",
      tabClass: 'tab2'
    }
  ];

  return (
    <>
      <section className="container practice-content">
        {tabContent.map((tab, index) => (
          <TabSection
            key={index}
            tabName={tab.name}
            tabClass={tab.tabClass}
            tabContent={tab.description}
            activeLink= {activeClass}
            onclick={updateActiveLink(tab.tabClass)}
          />
        ))}
      </section>
    </>
  );
}

export default Practice1Tab;
