import { useState } from "react"


function TabSection(props) {
  return (
    <>
      <div className="content {props.class}">
        <div className="name">Working {props.tabName}</div>
        <div className="content">
          {props.tabContent}
        </div>
      </div>
    </>
  )
}

function Practice1Tab() {
  const [ActiveClass, setActiveClass] = useState()
  const tabContent = [
    {
      name: "Why React?",
      description: "Component-based architecture: React follows a component-based architecture, allowing developers to build UI elements as reusable and self-contained components. This promotes code reusability, modularity, and easier maintenance.",
      class: 'tab1'
    },
    {
      name: "Core Feature",
      description: "Another feature description.",
      class: 'tab2'
    }
  ];

  return (
    <>
      <section className='container practice-content'>
        {tabContent.map((tab, index) => (
            <TabSection key={index} tabName={tab.name} tabContent={tab.description} />
          ))}
      </section>
    </>
  )
}

export default Practice1Tab;
