import './styles.scss';
import Header from './components/Header';
import Banner from './components/Banner';
import About from './components/About';
import TextForm from './components/TextForm';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './App.css';
import { useState, useEffect } from 'react';
import Alert from './components/Alert';

function App() {
  const [mode, setMode] = useState('light');
  const [showAlert, setShowAlert] = useState(true);

  const [alert, setAlert] = useState({
    msg: 'Grab The Amazing Offers.',
    class: 'primary',
    type: 'Success'
  })

  const handleCloseAlert = () => {
    setShowAlert(false);
  }

  const toggleMode = () => {
    setShowAlert(true);

    if (mode == 'dark') {
      setMode('light');
      document.body.classList.remove('dark-mode');
      setAlert({
        msg: 'Light Mode On.',
        class: 'primary',
        type: 'Success'
      })
    } else {
      setMode('dark');
      document.body.classList.add('dark-mode');
      setAlert({
        msg: 'Dark Mode On.',
        class: 'primary',
        type: 'Success'
      })
    }
  }

  const user = {
    name: "Test",
    lastName: "Verma"
  }
  // how to use createElement 
  // const ele = React.createElement(element.type, element.props, onclick: function() {})
  // return ele;

  // JSX code
  return (
    <>
      {/* passing data to child component and toggleMode is a eventlistener function here */}
      <Header uData={user} person={{ name: 'Lin Lanying', imageId: '1bX5QH6' }} mode={mode} toggleMode={toggleMode} />
      <Alert showAlert={showAlert} onClose={handleCloseAlert} msg={alert.msg} class={alert.class} type={alert.type} />
      <TextForm heading={'Test'} showAlert={setAlert} />
      <About/>
    </>
  );
}

export default App;

