import React from 'react'
import { useState } from 'react';

export default function TextForm() {
  const [upperCasetext, setUpperCasetext] = useState('text');

  const updateText = (value) => {
    setUpperCasetext(value.toUpperCase());
  } 

  return (
    <div>
      <>
        <div className='container'>
          <div className="mb-3">
            <textarea className="form-control" rows="5" id="text" onChange={() => {updateText()}}></textarea>
          </div>
          <button className="btn btn-primary">Convert to uppercase</button>
        </div>
      </>
    </div>
  )
}
